import { Map } from 'immutable';

const roverStore = Map({
    user: { name: "Student" },
    rovers: ['Curiosity', 'Opportunity', 'Spirit'],
    frontImage: ['assets/img/Curiosity.jpg', 'assets/img/Opportunity.jpg', 'assets/img/Spirit.jpg']
});

const returnState = function (returnCode, returnValue) {
    this.code = returnCode;
    this.value = returnValue; 
};

// add our markup to the page
const root = document.getElementById('root')

const updateStore = (store, newState) => {
    return store.merge(store, newState);
}

const render = async (root, state) => {
    if (state.code) {
        Object.assign(root, {
            innerHTML: App(state.value)
        });
    } else {
        Object.assign(root, {
            innerHTML: errCallback(state.value)
        });
    }
}

const errCallback = (state) => {
    return `
        <header class="error">
            ${state.message}
        </header>
        <footer></footer>
    `;
};

// create content
const App = (state) => {
    return `
        <header></header>
        <main>
            ${Greeting(roverStore.user.name)}
            <section>
                <h2>Rovers Dashboard</h2>
                <p class="subtitle">Instruction</p>
                <p class="text">Click the tab in below to show corresponding rover information with interactive search</p>
                <div class="gallary">
                    ${ImageOfTheDay(state)}
                </div>
            </section>
        </main>
        <footer></footer>
    `
}

// listening for load event because page should load before any JS is called
window.addEventListener('load', () => {
    try {
        const dailyState = getImageOfTheDay();
        const result = new returnState(true, dailyState);
    } catch (err) {
        const result = new returnState(false, err);
    } finally {
        render(root, result);
    }
})

// ------------------------------------------------------  COMPONENTS

// Pure function that renders conditional information -- THIS IS JUST AN EXAMPLE, you can delete it.
const Greeting = (name) => {
    if (name) {
        return `
            <h1>Welcome, ${name}!</h1>
        `
    }

    return `
        <h1>Hello!</h1>
    `
}

// Example of a pure function that renders infomation requested from the backend
const ImageOfTheDay = (state) => {

    // If image does not already exist, or it is not from today -- request it again
    const today = new Date();
    const photodate = new Date(state.date);

    // check if the photo of the day is actually type video!
    if (state.media_type === "video") {
        return (`
            <p class="subtitle">See today's featured video <a href="${state.url}">here</a></p>
            <div class="desc">
                <p class="subtitle">${state.title}</p>
                <p class="text">${state.explanation}</p>
            </div>
        `);
    } else {
        return (`
            <a target="_blank" href="${state.url}">
                <img src="${state.url}" height="350px" width="auto"/>
            </a>
            <div class="desc">
                <p class="subtitle">${state.title}</p>
                <p class="text">${state.explanation}</p>
            </div>
        `)
    }
}

// ------------------------------------------------------  API CALLS

// API call to get today apod data in json
const getImageOfTheDay = () => {
    return fetch(`http://localhost:3000/apod`)
        .then(res => {
            if (!res.ok) {
                throw { 
                    message: `HTTP ${res.status} Code` 
                };
            }
            return res.json();
        })
        .catch(err => {
            if (err.message) {
                throw {
                    message: `API Call encounters unexpected error: ${err.message}`
                };
            } else {
                throw {
                    message: `API Call encounters unexpected error: ${err}`
                };
            }
        });
}
